templates.legacy_templates_raw:
{
    "output_template": {
        "selected_keyword": "string",
        "article_strategy": {
            "article_type": "string (comprehensive_guide | deep_dive | case_study | comparison | news_analysis | how_to)",
            "background_and_purpose": {
                "why_now": "string (なぜ今この記事が必要か？)",
                "target_pain": "string (読者のどんな悩みを解決するか？)",
                "key_message": "string (この記事で伝えたい核となるメッセージ)",
                "urgency": "string (高/中/低)"
            },
            "conversion_design": {
                "primary_cta": "string (主要なCTA)",
                "secondary_cta": "string (副次的なCTA、任意)",
                "conversion_points": ["string (どこで何を訴求するか)"]
            }
        },
        "word_count_settings": {
            "mode": "string (manual | ai_seo_optimized | ai_readability | ai_balanced)",
            "manual_word_count": "number | null (manualモードの場合のみ)",
            "ai_calculation_note": "string (ai_*モードの場合：工程3Cで算出。manualモードの場合：手動指定)"
        },
        "keyword_analysis": {
            "search_volume": "string (例: 月間1,200回)",
            "competition": "string (高/中/低)",
            "cv_distance": "string (遠い/中程度/近い)",
            "phase": "string (phase1/phase2/phase3)",
            "expected_cvr": "string (例: 2.5%)"
        },
        "article_type": "string (悩み解決型 / 情報提供型)",
        "persona_hypothesis": {
            "job_title": "string",
            "age": "string",
            "challenges": "string",
            "search_intent": "string"
        },
        "neuroscience_evaluation": {
            "phase": "string (phase1/phase2/phase3)",
            "brain_activation": "string (扁桃体/前頭前野/線条体)",
            "emotional_trigger": "string",
            "cognitive_load": "string (高/中/低)",
            "commitment_window": "string"
        },
        "behavioral_economics": {
            "loss_aversion_frame": "string (◎/○/△/✗)",
            "social_proof_compatibility": "string (◎/○/△/✗)",
            "authority": "string (◎/○/△/✗)",
            "consistency": "string (◎/○/△/✗)",
            "liking": "string (◎/○/△/✗)",
            "scarcity": "string (◎/○/△/✗)",
            "primary_principle": "string (最も有効な原則)"
        },
        "llmo_evaluation": {
            "ai_citation_probability": "string (高/中/低)",
            "question_format": "string (はい/いいえ)",
            "voice_search_optimized": "string (はい/いいえ)",
            "section_independence": "string (高/中/低)",
            "structured_data_friendly": "string (はい/いいえ)"
        },
        "kgi_evaluation": {
            "cv_distance": "string (遠い/中程度/近い)",
            "expected_cvr": "string (例: 2.5%)",
            "cta_strategy": "string (3段階CTA戦略との整合性)",
            "estimated_monthly_inquiries": "string (例: 4.5件)",
            "cost_effectiveness_assessment": "string (高/中/低)"
        },
        // CTA設計（単一CTA or 段階的CTA）
        "cta_specification": {
            "design_type": "string (single OR staged)",
            "single_cta": {
                "url": "string (例: https://cross-learning.jp/)",
                "text": "string (例: クロスラーニングの詳細を見る)",
                "description": "string (例: クロスラーニング広報サイトのTOPページ)",
                "placement": {
                    "early": "number (650)",
                    "mid": "number (2800)",
                    "final": "string (target_word_count - 500前後)"
                }
            },
            "staged_cta": {
                "early": {
                    "url": "string (例: https://example.com/tool)",
                    "text": "string (例: 無料診断ツールを試す)",
                    "description": "string (例: 派遣社員教育の課題を診断できる無料ツール)",
                    "position": "number (650)"
                },
                "mid": {
                    "url": "string (例: https://example.com/download)",
                    "text": "string (例: 無料資料をダウンロード)",
                    "description": "string (例: 派遣社員教育の成功事例集（PDF）)",
                    "position": "number (2800)"
                },
                "final": {
                    "url": "string (例: https://example.com/contact)",
                    "text": "string (例: 無料相談に申し込む)",
                    "description": "string (例: 専門家による無料相談の申し込みフォーム)",
                    "position": "string (target_word_count - 500前後)"
                }
            },
            "constraint_notes": [
                "工程0のフォーム回答で指定されたCTAのみ使用",
                "AIが独自にCTAを考案することは禁止",
                "単一CTAの場合：全てのCTA（Early/Mid/Final）で同じURL・テキストを使用",
                "段階的CTAの場合：Early/Mid/Final で異なるURL・テキストを使用"
            ]
        },
        "article_specifications": {
            "max_word_count": "number (12000)",
            "target_word_count": "string (例: 8,000〜12,000字)",
            "writing_style": "string (例: 結論ファースト（各セクション冒頭で結論提示）)",
            "redundancy_check": "string (例: 記事完成後に冗長・重複箇所を指摘し添削)",
            "phase_distribution": {
                "phase1": "string (例: 800〜1,200字（不安喚起）)",
                "phase2": "string (例: 6,000〜9,000字（解決策提示）)",
                "phase3": "string (例: 1,200〜1,800字（行動促進）)"
            }
        },
        // ここまで追加
        "four_pillar_score": {
            "neuroscience": "number (0-5)",
            "behavioral_economics": "number (0-5)",
            "llmo": "number (0-5)",
            "kgi": "number (0-5)",
            "total": "number (0-20)"
        },
        "recommendation_reason": "string (3〜5行の推奨理由)",
        "alternative_keywords": [
            {
                "keyword": "string",
                "score": "number",
                "reason": "string"
            }
        ]
    },
    "keyword_candidate_table_template": {
        "headers": [
            "キーワード",
            "月間検索数",
            "競合性",
            "購買フェーズ",
            "CVR期待値",
            "行動経済学",
            "LLMO評価",
            "CTA戦略"
        ],
        "example_row": {
            "keyword": "派遣社員 教育方法",
            "search_volume": "1,200",
            "competition": "中",
            "phase": "Phase2",
            "cvr": "2.5%",
            "behavioral_economics": "損失回避◎",
            "llmo": "高",
            "cta_strategy": "デフォルトCTA適用"
        }
    },
    "completion_message_template": {
        "message": "✅ 工程0完了",
        "separator": "━━━━━━━━━━━━━━━━━━━━━━",
        "four_pillar_check": {
            "title": "4本柱チェック: 全て確認済み",
            "neuroscience": "✓",
            "behavioral_economics": "✓",
            "llmo": "✓",
            "kgi": "✓"
        },
        "cta_specification_check": {
            "title": "CTA仕様: 確認済み",
            "default_cta_url": "string",
            "default_cta_text": "string",
            "exception_applicable": "boolean"
        },
        "article_specifications_check": {
            "title": "記事仕様: 確認済み",
            "max_word_count": "12,000字",
            "writing_style": "結論ファースト",
            "redundancy_check": "完成後に実施"
        },
        "summary": {
            "selected_keyword": "string",
            "expected_cvr": "string",
            "phase": "string",
            "cta_strategy": "デフォルトCTA適用 or カスタムCTA適用"
        },
        "next_step": "次の工程: 工程1（競合記事取得・GAS自動実行 → 工程1_1.1統合_上位10サイト競合記事本文.csv を出力）",
        "important_notes": [
            "全工程でCTA仕様を厳守してください",
            "文字数は8,000〜12,000字を目標にしてください",
            "各セクション冒頭で結論を提示してください"
        ]
    }
}

examples.legacy_examples_raw:
{
    "example_1_phase2_keyword": {
        "input": {
