examples.legacy_examples_raw:
{
    "example_1_complete_section_with_four_pillars": {
        "scenario": "4本柱を完全に埋め込んだH2セクションの例",
        "section": {
            "section_id": "S01",
            "h2": "派遣社員の教育が失敗する3つの理由",
            "word_count_target": 600,
            "token_count_target": 470,
            "psychological_phase": "phase1_anxiety",
            "four_pillars": {
                "neuroscience": {
                    "cognitive_load": "3概念（体系的プログラム欠如、バランス不足、フィードバック不足）",
                    "phase": "phase1_anxiety",
                    "emotional_trigger": "損失データ（教育時間40時間×2,500円=10万円無駄、1人離職で55万円損失）による不安喚起と危機感醸成",
                    "sentence_length": "20-35文字/文を厳守"
                },
                "behavioral_economics": {
                    "principles": [
                        "loss_aversion",
                        "authority"
                    ],
                    "implementation": "各失敗による具体的損失額を明示（10万円、55万円）。厚生労働省データで権威性を担保し、信頼性を高める。",
                    "data_used": "PS001（離職率29.3%）、PS003（損失額55万円）、PS004（教育効果30%低下）"
                },
                "llmo": {
