{
    "example_1_long_sentence_split": {
        "scenario": "長文の分割",
        "before": {
            "text": "2024年12月発表の厚生労働省「令和6年派遣労働者実態調査」によれば、派遣社員の3ヶ月以内離職率は29.3%（前年比+3.2pt）に達し、過去5年間で最も高い水準となっている。",
            "character_count": 82,
            "problems": [
                "1文が82文字と長すぎる",
                "読みにくい"
            ]
        },
        "after": {
            "text": "2024年12月発表の厚生労働省「令和6年派遣労働者実態調査」によれば、派遣社員の3ヶ月以内離職率は29.3%（前年比+3.2pt）に達しています。これは過去5年間で最も高い水準です。",
            "character_count": "1文目: 63文字、2文目: 19文字",
            "improvements": [
                "2文に分割",
                "各文が60文字前後",
                "読みやすくなった",
                "データは一切変更していない"
            ]
        },
        "maintained": [
            "データ: 29.3%（前年比+3.2pt）",
            "引用: 厚生労働省「令和6年派遣労働者実態調査」2024年12月",
            "内容: 過去5年間で最も高い水準"
        ]
    },
    "example_2_terminology_supplement": {
        "scenario": "専門用語の補足",
        "before": {
            "text": "OJTとOff-JTを組み合わせることで効果が1.8倍向上します。",
            "problems": [
                "OJTとOff-JTの意味が不明",
                "初見の読者には分かりにくい"
            ]
        },
        "after": {
            "text": "OJT（実務を通じた教育）とOff-JT（座学研修）を組み合わせることで、教育効果が1.8倍向上します。",
            "improvements": [
                "OJT → OJT（実務を通じた教育）",
                "Off-JT → Off-JT（座学研修）",
                "「効果」→「教育効果」（明確化）",
                "初見の読者にも分かりやすい"
            ]
        },
        "maintained": [
            "データ: 1.8倍",
            "内容: 組み合わせることで向上"
        ]
    },
    "example_3_natural_expression": {
        "scenario": "表現の自然化",
        "before": {
            "text": "教育投資額と定着率の間には正の相関が認められる。",
            "problems": [
                "硬い表現（正の相関が認められる）",
                "「である」調"
            ]
        },
        "after": {
            "text": "教育への投資額が増えるほど、定着率も向上する傾向があります。",
            "improvements": [
                "「正の相関が認められる」→「増えるほど、向上する傾向」",
                "分かりやすい表現",
                "「である」調→「です・ます」調"
            ]
        },
        "maintained": [
            "内容: 教育投資額と定着率の関係"
        ]
    },
    "example_4_connection_improvement": {
        "scenario": "接続の改善",
        "before": {
            "text": "離職率が高い。教育が必要。体系的なプログラムを導入すべき。",
            "problems": [
                "接続詞なし",
                "文の流れが不自然",
                "ぶつ切り"
            ]
        },
        "after": {
            "text": "離職率が高いという課題があります。そのため、適切な教育が必要です。具体的には、体系的なプログラムを導入することが効果的です。",
            "improvements": [
                "接続詞の追加（そのため、具体的には）",
                "文の流れがスムーズ",
                "論理的なつながり"
            ]
        },
        "maintained": [
            "内容: 離職率が高い→教育が必要→体系的なプログラム導入"
        ]
    },
    "example_5_redundancy_reduction": {
        "scenario": "冗長な表現の簡潔化",
        "before": {
            "text": "教育に費やした時間40時間に人件費2,500円を掛けると10万円となり、これが無駄になっているケースが多く見られます。",
            "problems": [
                "計算の説明が冗長",
                "読みにくい"
